INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('A', 'Aegrotat', 'Aegrotat Standing. The student has been granted exemption from writing a Provincial Exam due to unpredictable circumstances that render a student unable to write an exam even at a future session. ', 1, TIMESTAMP'1984-07-01 00:00:00.0', TIMESTAMP'2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('B', 'Bogus', 'When the school reported the same school percentage in more than one session, but the student did not retake the course. For example, if a school reported 65% for both January and April for a given examinable course, TRAX would recognize a duplication, considered the second superfluous and mark that record’s classroom mark with a B. The B course would be ignored for mixing and matching. ', 2, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('C', 'Challenge', 'Used for students wishing to obtain a scholarship score by challenging a provincially examinable course. After the 1997-98 implementation of the new Course Challenge Policy, this practice was phased out. ', 3, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2001-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('D', 'Deferred', 'Indicates that the student was granted a deferral for writing the exam because of illness, accident or personal difficulty. ', 4, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('Q', 'Disqualified', 'Student is believed to have breached one or more rules during a provincial exam and any result was disqualified. ', 5, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('N', 'None', 'Indicates no special case for the provincial exam. ', 6, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('R', 'Rewrite', 'Student re-wrote the exam but did not re-take the course. ', 7, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('F', 'Special Case F', 'Unknown. Not applied after 1986. ', 8, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '1986-06-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('W', 'Special Case W', 'Unknown. Not applied after 1986. ', 9, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '1986-08-30 00:00:00.0');
INSERT INTO EXAM_SPECIAL_CASE_CODE (EXAM_SPECIAL_CASE_CODE, LABEL, DESCRIPTION, DISPLAY_ORDER, EFFECTIVE_DATE, EXPIRY_DATE) VALUES('V', 'Verified', 'Student re-took the course and received the exact same school percent as last time. Initially flagged as "B", but changed to V when the student’s classroom mark was confirmed. ', 10, TIMESTAMP '1984-07-01 00:00:00.0', TIMESTAMP '2020-06-30 00:00:00.0');
